mapper.insert:
/** insertFileMaster    */
insert into file_master (file_type, description, crt_id, crt_ip)
values (#{fileType}, #{description}, #{crtId}, #{crtIp})

/** insertFileDetail    */
insert into file_detail (master_seq, file_name, file_path, file_size, mime_type, crt_id, crt_ip)
values (#{masterSeq}, #{fileName}, #{filePath}, #{fileSize}, #{mimeType}, #{crtId}, #{crtIp})
